mapper.insert:
/* kr.or.ddit.employee.mapper.EmployeeMapper.insertEmp */
INSERT INTO
	tb_employee (
		emp_no
		, emp_nm
		, emp_email
		, emp_Password
		, emp_telno
		, emp_brthdy
		, emp_addr
		, emp_dtl_addr
		, emp_zip
		, dept_code
	)
VALUES (
	  #{empNo}
	, #{empNm}
	, #{empEmail}
	, #{empPassword}
	, #{empTelno}
	, #{empBrthdy}
	, #{empAddr}
	, #{empDtlAddr}
	, #{empZip}
	, #{deptCode}
)

mapper.update:

UPDATE
    tb_employee
SET
    emp_email = #{empEmail}
    , emp_telno = #{empTelno}
    , emp_addr = #{empAddr}
    , emp_dtl_addr = #{empDtlAddr}
    , emp_zip = #{empZip}
    , emp_rtcpn = #{empRtcpn}
    , emp_resign_ysno =
    	CASE WHEN
    		#{empRtcpn} IS NULL THEN '0'
        ELSE
        	'1'
      END
WHERE
    emp_no = #{empNo}

mapper.select:
select count(emp_email) 
from tb_employee 
where emp_email = #{empEmail}

mapper.update:
update tb_employee
set emp_password = #{empPassword}
, first_login = 'Y'
where emp_no = #{empNo}

update TB_EMPLOYEE
set 
     EMP_PASSWORD = #{empPassword}

    , EMP_EMAIL =#{empEmail}

    , EMP_TELNO =#{empTelno}
    , EMP_ADDR =#{empAddr}
    , EMP_DTL_ADDR =#{empDtlAddr}
    , EMP_ZIP =#{empZip}
where emp_no=#{empNo}
