mapper.insert:
/* kr.or.ddit.board.mapper.NoticeMapper.insertNotice */

		SELECT NVL(MAX(TO_NUMBER(ntbd_code)), 0) + 1 FROM tb_notice_board

	INSERT INTO
		tb_notice_board (
			ntbd_code
			, writer_emp_no
			, ntbd_reg_date
			, ntbd_subject
			, ntbd_content
		)
	VALUES (
		#{ntbdCode}
		, #{writerEmpNo}
		, SYSDATE
		, #{ntbdSubject}
		, #{ntbdContent}
	)

mapper.update:
/* kr.or.ddit.board.mapper.NoticeMapper.updateNotice */
	UPDATE
		tb_notice_board
	SET
		ntbd_subject = #{ntbdSubject}
		, ntbd_content = #{ntbdContent}
	WHERE
		ntbd_code = #{ntbdCode}
