mapper.update:
/* updatePat */
	UPDATE 
		tb_patient
	SET 
		pat_name = #{patname}
		, pat_tel = #{pattel}
		, pat_addr = #{pataddr}
		, pat_dtl_addr = #{patdtladdr}
		, pat_zip = #{patzip}
	WHERE 
		pat_code = #{patCode}

mapper.delete:
/* deletePat */
	DELETE 
	FROM 
		tb_patient
	WHERE 
		pat_code = #{patCode}

mapper.select:
/*selectPatNoByName*/
SELECT
	pat_code
FROM
	tb_patient
WHERE
	pat_name = #{patName}
AND
	pat_ihidnum = #{patIhidnum}
