mapper.insert:
/* insertReceipt */
INSERT INTO 
	tb_receipt 
	(
		RCEPT_NO
		, PAT_CODE
		, RCEPT_PATH_CODE
		, RCEPT_FIRST_EXAM_CODE
		, RCEPT_DAY_CODE
		, RCEPT_MEMO
		, SCHD_NO
		, RECEPT_TIME
	)
VALUES
	(
		#{rceptNo}
		, #{patCode}
		, #{rceptPathCode}
		, #{rceptFirstExamCode}
		, #{rceptDayCode}
		, #{rceptMemo}
		, #{schdNo}
		, #{receptTime}
	)

/*insertClinic*/
SELECT TO_CHAR(sysdate,'YYMM') || '05' || TRIM(TO_CHAR(NVL( (SELECT MAX(TO_NUMBER(SUBSTR(treat_no,-3))) FROM tb_clinic WHERE treat_no LIKE TO_CHAR(sysdate,'YYMM')||'%') ,0)+1, '000')) as treat_no FROM dual
INSERT INTO TB_CLINIC ( treat_no ,rcept_no ) VALUES ( #{treatNo} ,#{rceptNo} )

mapper.update:
/* delWait */
	UPDATE 
		tb_clinic 
	SET 
		clnic_sttus_code = 'FAIL' 
	WHERE treat_no = #{treatNo}
